// Custom module declarations for assets

declare module '*.yaml' {
  const content: string
  export default content
}

declare module '*.yml' {
  const content: string
  export default content
}

declare module '*.png' {
  const content: string
  export default content
}

declare module '*.jpg' {
  const content: string
  export default content
}

declare module '*.svg' {
  const content: string
  export default content
}

declare module '*.txt' {
  const content: string
  export default content
}
